package JustPrograms;

public class PrintPattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int n=5;
		
			
			
			
			for(int j=0;j<=n;j++){
				//System.out.println();
				for(int i=n ;i>j;i--){
				 System.out.println(" *");	
				}
				System.out.println();
			}
			
		
	}

}
