package JustPrograms;

public class PrintPatternStar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=5,i,j;
		for(i=0; i<n; i++) { 
			System.out.println("*"); 
			for(j=0; j<=i; j++){ 
				System.out.print("* "); 
			} 
			
		} 


	}

}
